mapper.update:
UPDATE BankAccount
SET goal_id = NULL
WHERE goal_id = #{goalId, jdbcType=BINARY, javaType=java.util.UUID}

UPDATE BankAccount
SET goal_id = NULL
WHERE account_id = #{accountId}

UPDATE BankAccount
SET goal_id = #{goalId}
WHERE account_id = #{accountId} AND goal_id IS NULL

mapper.select:
SELECT *
FROM goal
WHERE goal_id = #{goalId, jdbcType=BINARY, javaType=java.util.UUID}
