mapper.update:
UPDATE inquiry
answered_content = #{inquiry.answeredContent}, is_answered = true
WHERE info_id = #{infoId, javaType=java.util.UUID, jdbcType=BINARY}

mapper.select:
SELECT *
FROM inquiry
WHERE info_id = #{infoId, javaType=java.util.UUID, jdbcType=BINARY}

SELECT *
FROM inquiry
ORDER BY created_at desc LIMIT #{offset}, #{amount}
