mapper.insert:
INSERT INTO notice(
notice_id, admin_id, title, content
) VALUES(
#{noticeId, javaType=java.util.UUID, jdbcType=BINARY},
#{adminId, javaType=java.util.UUID, jdbcType=BINARY},
#{title},
#{content}
)

mapper.select:
SELECT * FROM notice
ORDER BY created_at DESC
LIMIT #{offset}, #{amount}
